kickstart.post:
## ## ROCKS ## pid_file=/opt/nagios/var/nsca.pid server_port=5667 server_address=&Kickstart_PrivateAddress; nsca_user=nagios nsca_group=apache debug=0 command_file=/opt/nagios/var/rw/nagios.cmd alternate_dump_file=/opt/nagios/var/rw/nsca.dump aggregate_writes=0 append_to_file=0 max_packet_age=30 # We're restricting to local network; password would be overkill #password= decryption_method=1
nsca 5667/tcp # NSCA Nagios add-on
# default: on # description: NSCA (Nagios Service Check Acceptor) service nsca { flags = REUSE socket_type = stream wait = no user = nagios group = apache server = /opt/nagios/bin/nsca server_args = -c /opt/nagios/etc/nsca.cfg --inetd log_on_failure += USERID disable = no only_from = &Kickstart_PrivateAddress;/&Kickstart_PrivateNetmaskCIDR; cps = 400 10 }
/bin/chown -R nagios:apache /opt/nagios
/opt/rocks/bin/rocks set attr NSCA_ServerName &Kickstart_PrivateHostname;
